import React from "react";
import { BrowserRouter as Router, Link, Route } from "react-router-dom";
import AnaSayfa from "./AnaSayfa"
import Form from "./Form";
import "./index.css";
import "./form.css";
import "./App.css";


const App = () => {
  return (
    <>
      < Router>
        <div className="App">
          <h1>Teknolojik Yemekler</h1>
          <p><Link style={{ color: '#FA7070' }} to="/">Ana Sayfa</Link></p>
          <p><Link style={{ color: '#FA7070' }} to="/pizza">Sipari≈ü Formu</Link></p>
          <Route exact path="/:order pizza" component={AnaSayfa} />
        </div>

        <div>
          <Route path="/pizza" component={Form} />
        </div>

      </ Router>
    </>
  );
};


export default App;
