import React from "react";


export default function Form(props){

  return (
    <div>
     <p>
     <label htmlFor="pizza-form">Build Your Own Pizza: </label><br/>
          <select
            id="pizza-form"
            name="pizza-form"
           >
            <option value="">--- Choice of Size ---</option>
            <option value="1">Small</option>
            <option value="2">Medium</option>
            <option value="3">Large</option>
            </select>
            </p>
    </div>
  );
};
